import React, { useEffect, useState } from "react";
import "../../styles/Login.css"; // Assuming you've created the styles here
import { Container, Card } from "react-bootstrap";
import axios from "axios";

function Login() {
  const [user, setUser] = useState([]);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const handleSubmit = async(e) => {
    e.preventDefault();
    try{
      const response = await axios.post("http://localhost/user/login", {
        email,
        password
      });
      console.log("Login succesfully", response.data);
    }catch(error){
      console.error("Login failed")
    }
  }
    useEffect(() => {
       
        document.body.classList.add('login-page');
        
        return () => {
          document.body.classList.remove('login-page');
        };
      }, []);

      useEffect(() =>{
        const fetchUser = async () => {
          try{
            const response = await axios.get("http://localhost/user/login")
            setUser(response.data);
            console.log(response.data);
          }catch (error){
            console.log("Error in loggin: ", error);
          }
        }
      }, []);
    return (
        <Container className="d-flex justify-content-center align-items-center min-vh-100">
          <Card className="login-card shadow-lg p-4">
            <h2 className="text-center mb-4">Login</h2>
            <form className="login-form">
              <input type="text" placeholder="Email" className="form-control mb-3" onChange = {(e) => setEmail(e.target.value) } />
              <input type="password" placeholder="Password" className="form-control mb-3" onChange = {(e) => setPassword(e.target.value)} />
              <button type="submit" className="btn btn-primary w-100" onClick = {handleSubmit}>Login</button>
            </form>
            <div className="signup-link mt-3 text-center">
              <p> <a href="/">Enter as a guest</a></p>
            </div>
            <div className="signup-link mt-3 text-center">
              <p>Don't have an account? <a href="/register">Sign up</a></p>
            </div>
          </Card>
        </Container>
      );
}

export default Login;
