import React, { useEffect, useState } from 'react';
import { Button, Form, Dropdown, Alert, ListGroup, Container, Row, Col } from 'react-bootstrap';
import axios from 'axios';
import NavBar from "../../components/Navbar";

function GroupChatApp() {
    const [groups, setGroups] = useState([]);
    const [selectedGroup, setSelectedGroup] = useState('');
    const [messages, setMessages] = useState([]);
    const [inputValue, setInputValue] = useState('');
    const [notifications, setNotifications] = useState([]);

    // Fetch available groups once on component mount
    useEffect(() => {
        const fetchGroups = async () => {
            try {
                const response = await axios.get('http://localhost:8080/group');
                setGroups(response.data);
            } catch (err) {
                console.error('GET /group failed:', err);
            }
        };
        fetchGroups();
    }, []);

    // Automatically fetch messages when selectedGroup changes
    useEffect(() => {
        if (!selectedGroup) {
            setMessages([]); // Clear messages if no group selected
            return;
        }

        // Find selected group's id
        const group = groups.find(g => g.name === selectedGroup);
        if (!group || !group.id) return;

        const fetchMessages = async () => {
            try {
                const response = await axios.get(`http://localhost:8080/message/group/${group.id}`);
                setMessages(response.data);
            } catch (err) {
                console.error('Failed to fetch messages:', err);
            }
        };

        fetchMessages();
    }, [selectedGroup, groups]);

    const handleSend = async () => {
        if (!inputValue.trim() || !selectedGroup) return;

        const group = groups.find(g => g.name === selectedGroup);
        if (!group) {
            console.error("Selected group not found");
            return;
        }

        const messagePayload = {
            content: inputValue
        };

        try {
            await axios.post(`http://localhost:8080/message/post?groupChatId=${group.id}`, messagePayload);

            // After sending, reload messages automatically
            const response = await axios.get(`http://localhost:8080/message/${group.id}`);
            setMessages(response.data);

            setInputValue('');
        } catch (err) {
            console.error('Sending message failed:', err);
        }
    };

    const handleCreateGroup = async (e) => {
        e.preventDefault();
        if (!inputValue.trim()) return;

        try {
            await axios.post('http://localhost:8080/group/create', { name: inputValue });
            setGroups(prev => [...prev, { name: inputValue, id: Date.now() }]); // Note: ideally get id from response
            setSelectedGroup(inputValue);
            setInputValue('');
        } catch (err) {
            console.error('Create group error:', err);
        }
    };

    return (
        <div>
            <NavBar />
            <Container className="mt-4">
                <h3>Group Chat App</h3>

                <Row className="mb-3">
                    <Col md={6}>
                        <Dropdown onSelect={setSelectedGroup}>
                            <Dropdown.Toggle variant="primary">
                                {selectedGroup || 'Select Group'}
                            </Dropdown.Toggle>
                            <Dropdown.Menu>
                                {groups.map((g, i) => (
                                    <Dropdown.Item key={i} eventKey={g.name}>{g.name}</Dropdown.Item>
                                ))}
                            </Dropdown.Menu>
                        </Dropdown>
                    </Col>

                    <Col md={6}>
                        <Form onSubmit={handleCreateGroup}>
                            <Form.Control
                                type="text"
                                placeholder="Create new group"
                                value={inputValue}
                                onChange={e => setInputValue(e.target.value)}
                            />
                            <Button type="submit" className="mt-2">Create Group</Button>
                        </Form>
                    </Col>
                </Row>

                {selectedGroup && (
                    <>
                        <h5>Messages in {selectedGroup}</h5>
                        <ListGroup className="mb-3" style={{ maxHeight: '300px', overflowY: 'auto' }}>
                            {messages.map((msg, idx) => (
                                <ListGroup.Item key={idx}>{msg.content}</ListGroup.Item>
                            ))}
                        </ListGroup>

                        <Form className="d-flex mb-3" onSubmit={e => { e.preventDefault(); handleSend(); }}>
                            <Form.Control
                                type="text"
                                placeholder="Enter message"
                                value={inputValue}
                                onChange={e => setInputValue(e.target.value)}
                            />
                            <Button type="submit" className="ms-2">Send</Button>
                        </Form>
                    </>
                )}

                <h5>Notifications</h5>
                <ListGroup>
                    {notifications.map((note, i) => (
                        <ListGroup.Item key={i}>{note}</ListGroup.Item>
                    ))}
                </ListGroup>
            </Container>
        </div>
    );
}

export default GroupChatApp;
