import React, { use, useEffect, useState } from "react";
import "../../styles/Register.css"; // Make sure to create a new Register.css
import { Container, Card, Form } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function Register() {
  const [first, setFirst] = useState('');
  const [second, setSecond] = useState('');
  const [mail, setMail] = useState('');
  const [pass, setPass] = useState('');
  const [age, setAge] = useState('');
  const [gender, setGender] = useState('');
  const [emailErr, setEmailErr] = useState(true);
  const navigate = useNavigate();
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

function validateEmail(email) {
   setEmailErr(emailRegex.test(email));
}

  const handleSubmit = async(e) => {
    e.preventDefault();
    console.log(first,second,mail,pass,age,gender)
    try{
      const response = await axios.post("http://localhost:8080/user/add", {
        "firstName":first,
        "lastName":second,
        "email":mail,
        "password":pass,
        age,
        gender
      });
      console.log(response.data)
      navigate("/login");
    }catch(error){
      console.log("Register failed", error);
    }
  }
  useEffect(() => {
  
    document.body.classList.add('register-page');
    
   
    return () => {
      document.body.classList.remove('register-page');
    };
  }, []);

  return (
    <Container className="d-flex justify-content-center align-items-center min-vh-100">
      <Card className="register-card shadow-lg p-4">
        <h2 className="text-center mb-4">Register</h2>
        <form className="register-form" onSubmit={handleSubmit}>  
          <div className= "d-flex">
          <input type="text" placeholder="First Name" className="form-control me-3"onChange= {(e)=> setFirst(e.target.value)} />
          <input type="text" placeholder="Last Name" className="form-control mb-3" onChange= {(e)=> setSecond(e.target.value)} />
          </div>

          <input type="mail" placeholder="Email" className={`form-control mb-3 ${emailErr? 'border border-danger': ''}`} onChange= {(e) =>{validateEmail(e.target.value); setMail(e.target.value)}} />
          <input type="password" placeholder="Password" className="form-control mb-3" onChange= {(e)=> setPass(e.target.value)} />
          <input type="text" placeholder="Age" className="form-control mb-3" onChange= {(e) => setAge(e.target.value)} />

          <Form.Select className = "mb-3" onChange={(e) => setGender(e.target.value)}>
            <option value="NO_SPECIFIC" >Gender</option>
            <option value="MALE">Male</option>
            <option value="FEMALE">Female </option>
            <option value="NO_SPECIFIC">Don't specify</option>

          </Form.Select>
          
          <button type="submit" className="btn btn-primary w-100">Register</button>
        </form>
        <div className="login-link mt-3 text-center">
          <p>Already have an account? <a href="/login">Login</a></p>
        </div>
      </Card>
    </Container>
  );
}

export default Register;
