import { useContext } from 'react';
import { UserContext } from '../../pages/Home/AdminBoard';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';


function UserViewModal(props) {
    const {viewUser, notebook} = useContext(UserContext); 
    if (!viewUser) {
      return null;
    }
    
    return (
      <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            User Info
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          
          <p><strong>Id:</strong> {notebook.user}</p>
          {/* <p><strong>Name:</strong> {viewUser.firstName} {viewUser.lastName}</p>
          <p><strong>Email:</strong> {viewUser.email}</p>
          <p><strong>Password:</strong> {viewUser.password}</p>
          <p><strong>Gender:</strong> {viewUser.gender}</p> */}
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={props.onHide}>Close</Button>
        </Modal.Footer>
      </Modal>
    );
  }
  
  export default UserViewModal;
