import React, { createContext, useEffect, useState } from "react";
import "../../styles/Login.css"; // Assuming you've created the styles here
import { Container, Card } from "react-bootstrap";
import {useNavigate} from "react-router-dom";
import axios from "axios";

export const UserContext = createContext();
function Login() {
  const [loggedUser, setLoggedUser] = useState([]);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errFlag, setErrFlag] = useState(null);
  const navigate = useNavigate();
  const handleSubmit = async(e) => {
    
    e.preventDefault();
    try{
      const response = await axios.post("http://localhost:8080/user/login", {
        email,
        password
      });
      console.log(response.data)
      setLoggedUser(response.data)
      setErrFlag(false);
      navigate("/");
    }catch(error){
      console.error("Login failed", error);
      setErrFlag(true);
    }
  }
    useEffect(() => {
       
        document.body.classList.add('login-page');
        
        return () => {
          document.body.classList.remove('login-page');
        };
      }, []);

    return (
        <Container className="d-flex justify-content-center align-items-center min-vh-100">
          <Card className="login-card shadow-lg p-4">
            <h2 className="text-center mb-4">Login</h2>
            <form className="login-form" onSubmit={handleSubmit}>
              <input type="text" placeholder="Email" className="form-control mb-3" onChange = {(e) => setEmail(e.target.value) } />
              <input type="password" placeholder="Password" className="form-control mb-3" onChange = {(e) => setPassword(e.target.value)} />
              <button type="submit" className="btn btn-primary w-100">Login</button>
          
              {errFlag && 
            <p className="text-bg-danger mt-2">Invalid credentials </p>}
        
            </form>
            <div className="signup-link mt-3 text-center">
              <p> <a href="/">Enter as a guest</a></p>
            </div>
            <div className="signup-link mt-3 text-center">
              <p>Don't have an account? <a href="/register">Sign up</a></p>
            </div>
          </Card>
        </Container>
      );
}

export default Login;
