import React, { createContext, useContext, useEffect, useState } from "react";
import "../../styles/Login.css";
import { Container, Card } from "react-bootstrap";
import {useNavigate} from "react-router-dom";
import axios from "axios";
import { LoggedUserContext } from "../../context/LoggedUserContext";

function Login() {
  const {setLoggedUser} = useContext(LoggedUserContext); 
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errFlag, setErrFlag] = useState(null);
  const [emailErr, setEmailErr] = useState(true);
  const [passErr, setPassErr] = useState(true);
  const navigate = useNavigate();
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  const passwordRegex = /^(?=.*[0-9])(?=.*[!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?])/;

  function validateEmail(email) {
    setEmailErr(emailRegex.test(email));
  }

  function validatePassword(password) {
    setPassErr(passwordRegex.test(password));
  }

  const handleSubmit = async(e) => {
    
    e.preventDefault();
    try{
      const response = await axios.post("http://localhost:8080/user/login", {
        email,
        password
      });
      console.log(response.data)
      setLoggedUser(response.data)
      setErrFlag(false);
      navigate("/");
    }catch(error){
      console.error("Login failed", error);
      setErrFlag(true);
    }
  }
    useEffect(() => {
       
        document.body.classList.add('login-page');
        
        return () => {
          document.body.classList.remove('login-page');
        };
      }, []);

    return (
        <Container className="d-flex justify-content-center align-items-center min-vh-100">
          <Card className="login-card shadow-lg p-4">
            <h2 className="text-center mb-4">Login</h2>
            <form className="login-form" onSubmit={handleSubmit}>
              <input 
                type="email" 
                placeholder="Email" 
                className={`form-control mb-3 ${!emailErr? 'border border-danger': ''}`}
                onChange = {(e) => {validateEmail(e.target.value); setEmail(e.target.value)}} 
                required
              />
              <input 
                type="password" 
                placeholder="Password" 
                className={`form-control mb-3 ${!passErr? 'border border-danger': ''}`}
                onChange = {(e) => {validatePassword(e.target.value); setPassword(e.target.value)}}
                required
                minLength="3"
                maxLength="20"
                title="Password must contain at least 1 number and 1 special character"
              />
              <button type="submit" className="btn btn-primary w-100">Login</button>
          
              {errFlag && 
            <div>
            <p className="text-bg-danger mt-2">Invalid credentials </p>
            <p className= "signup-link mt-3 text-center">

              <a href= "/recover">
                Forgot password
              </a>  
            </p>
            </div>

              }
        
            </form>
            <div className="signup-link mt-3 text-center">
              <p> <a href="/">Enter as a guest</a></p>
            </div>
            <div className="signup-link mt-3 text-center">
              <p>Don't have an account? <a href="/register">Sign up</a></p>
            </div>
            
          </Card>
        </Container>
      );
}


export default Login;
