import { useContext } from 'react';
import { UserContext } from '../../pages/Home/AdminBoard';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import NavBar from '../Navbar';


function UserViewModal(props) {
    const {viewUser, notebook} = useContext(UserContext); 
    if (!viewUser) {
      return null;
    }
   
    return (
      <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            User Info
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <h5>User's Notebook: </h5>
          {notebook.length > 0 ? (
            <ul>
              {notebook.map(nb => (
                <li key={nb.id}>
                  <strong>ID:</strong> {nb.id}
                </li>
                
               
              ))}
            </ul>
          ) : (
            <p>No notebooks available for this user</p>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={props.onHide}>Close</Button>
        </Modal.Footer>
      </Modal>
    );
  }
  
  export default UserViewModal;
