import React, { useEffect, useState } from 'react';
import { Button, Form, Dropdown, Alert, ListGroup, Container, Row, Col } from 'react-bootstrap';
import SockJS from 'sockjs-client';
import { Client } from '@stomp/stompjs';
import axios from 'axios';
import NavBar from "../../components/Navbar"
function GroupChatApp() {
    const [groups, setGroups] = useState([]);
    const [selectedGroup, setSelectedGroup] = useState('');
    const [message, setMessage] = useState('');
    const [inputValue, setInputValue] = useState('');
    const [notifications, setNotifications] = useState([]);
    const [stompClient, setStompClient] = useState(null);

    // Fetch available groups
    useEffect(() => {
        const response  = axios.get('http://localhost:8080/group')
        console.log(response.data)
    }, []);

    // // Initialize WebSocket
    // useEffect(() => {
    //     const socket = new SockJS('http://localhost:8080/socket');
    //     const client = new Client({
    //         webSocketFactory: () => socket,
    //         debug: console.log,
    //         onConnect: () => console.log('WebSocket connected'),
    //     });

    //     client.activate();
    //     setStompClient(client);

    //     return () => client.deactivate();
    // }, []);

    // Subscribe to group topic when selectedGroup changes
    // useEffect(() => {
    //     if (!stompClient || !selectedGroup) return;

    //     const subscription = stompClient.subscribe(`/topic/group/${selectedGroup}`, (msg) => {
    //         const parsed = JSON.parse(msg.body);
    //         setMessage(parsed.message);
    //         setNotifications(prev => [...prev, parsed.message]);
    //     });

    //     return () => subscription.unsubscribe();
    // }, [selectedGroup, stompClient]);

    // const handleSend = () => {
    //     if (!inputValue.trim() || !selectedGroup) return;

    //     axios.post(`http://localhost:8080/group/${selectedGroup}/message`, {
    //         message: inputValue
    //     })
    //     .then(() => setInputValue(''))
    //     .catch(err => console.error('Sending error:', err));
    // };

    const handleCreateGroup = async(e) => {
        e.preventDefault();
        if (!inputValue.trim()) return;

        // axios.post('http://localhost:8080/group/create', { name: inputValue })
        //     .then(res => {
        //         setGroups(prev => [...prev, inputValue]);
        //         setSelectedGroup(inputValue);
        //         setInputValue('');
        //     })
        //     .catch(err => console.error('Create group error:', err));
        const response = await axios.post("http://localhost:8080/group/create")
    };

    return (
        <div>
        <NavBar/>
        <Container className="mt-4">
            <h3>Group Chat App</h3>

            {/* Group Selector */}
            <Row className="mb-3">
                <Col md={6}>
                    <Dropdown onSelect={setSelectedGroup}>
                        <Dropdown.Toggle variant="primary">
                            {selectedGroup || 'Select Group'}
                        </Dropdown.Toggle>
                        <Dropdown.Menu>
                            {groups.map((g, i) => (
                                <Dropdown.Item key={i} eventKey={g}>{g}</Dropdown.Item>
                            ))}
                        </Dropdown.Menu>
                    </Dropdown>
                </Col>

                {/* Create Group */}
                <Col md={6}>
                    <Form onSubmit={handleCreateGroup}>
                        <Form.Control
                            type="text"
                            placeholder="Create new group"
                            value={inputValue}
                            onChange={e => setInputValue(e.target.value)}
                        />
                        <Button type="submit" className="mt-2">Create Group</Button>
                    </Form>
                </Col>
            </Row>

            {/* Messaging */}
            {selectedGroup && (
                <>
                    <Alert variant="info">Latest message: {message}</Alert>

                    <Form className="d-flex mb-3">
                        <Form.Control
                            type="text"
                            placeholder="Enter message"
                            value={inputValue}
                            onChange={e => setInputValue(e.target.value)}
                        />
                        {/* <Button onClick={handleSend} className="ms-2">Send</Button> */}
                    </Form>
                </>
            )}

            {/* Notifications */}
            <h5>Notifications</h5>
            <ListGroup>
                {notifications.map((note, i) => (
                    <ListGroup.Item key={i}>{note}</ListGroup.Item>
                ))}
            </ListGroup>
        </Container>
        </div>
    );
}

export default GroupChatApp;
