import { useContext, useState } from 'react';
import { UserContext } from '../../pages/Home/AdminBoard';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Card from 'react-bootstrap/Card';
import Stack from 'react-bootstrap/Stack';
import { useSwipeable } from 'react-swipeable'; // Importing useSwipeable hook for swipe gestures

import "../../styles/NotebookView.css"
import axios from 'axios';

function UserViewModal(props) {
  const { viewUser, notebook, setNotebooks } = useContext(UserContext);
  const [swiping, setSwiping] = useState(false); // Track whether the card is being swiped
  const [swipeDirection, setSwipeDirection] = useState(0); // 1 for left, -1 for right

  // Async function to handle notebook deletion
  const handleDeleteNotebook = async (nb) => {
    try {
      const response = await axios.post("http://localhost:8080/notebooks/delete", nb);
      setNotebooks(response.data); // Update notebooks after deletion
    } catch (error) {
      console.error("Error deleting notebook:", error);
    }
  };

  // Handling swipe gestures and invoking the delete function on swipe left
  const swipeHandlers = useSwipeable({
    onSwipedLeft: (eventData) => {
      setSwiping(true);
      setSwipeDirection(1); // Swipe left
      const nb = eventData.event.target.closest('.notebook-entry').dataset.notebook;
      if (nb) {
        handleDeleteNotebook(JSON.parse(nb));  // Parsing and passing the notebook object
      }
    },
    onSwipedRight: (eventData) => {
      setSwiping(true);
      setSwipeDirection(-1); // Swipe right
      // You can handle additional logic for right swipe if needed
    },
    preventDefaultTouchmoveEvent: true,
    trackMouse: true,
  });

  if (!viewUser) {
    return null;
  }

  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          User Info
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <h5 className="mb-4">User's Notebooks</h5>
        {notebook.length > 0 ? (
          <Stack gap={3}>
            {notebook.map((nb) => (
              <div
                key={nb.id}
                {...swipeHandlers}
                className="notebook-entry"
                data-notebook={JSON.stringify(nb)} // Storing notebook object as a data attribute
                style={{
                  transform: `translateX(${swipeDirection * 100}%)`, // Applying translateX for swipe effect
                  transition: swiping ? 'transform 0.3s ease' : 'none', // Smooth transition when swiping
                }}
              >
                <Card className="notebook-entry shadow-sm">
                  <Card.Body>
                    <Card.Title className="text-primary">Notebook #{nb.id}</Card.Title>
                    <Card.Text><strong>Title:</strong> {nb.notebookName}</Card.Text>
                    <Card.Text><strong>Notes:</strong> {nb.notes}</Card.Text>
                    <Card.Text><strong>Resolution:</strong> {nb.resolution}</Card.Text>
                  </Card.Body>
                </Card>
              </div>
            ))}
          </Stack>
        ) : (
          <p className="text-muted">No notebooks available for this user.</p>
        )}
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={props.onHide}>Close</Button>
      </Modal.Footer>
    </Modal>
  );
}

export default UserViewModal;
