export const UserContext = createContext();
function DashBoard() {
    const [users, setUsers] = useState([]);
    const [notebook, setNotebooks] = useState([]);
    const [viewUser, setViewUser] = useState(null);
    const [loadState, setLoadState] = useState(true);
    const [infoModal, setInfoModal] = useState(false);
    const fetchUsers = async () => {
        setLoadState(true);
        try {
            const response = await axios.get("http://localhost:8080/user");
            
            console.log('Fetched users:', response.data);  
            setUsers(response.data);                       
        } catch (error) {
            console.error("Error fetching users:", error);
        }finally{
            setLoadState(false);
        }
    };
    useEffect(() => {
        fetchUsers();
        
    }, []);

    const handleViewProfile = async(user) =>{
        setViewUser(user);
        setInfoModal(true);
        

        try{
            const response = await axios.get("http://localhost:8080/notebooks", {
                params: {
                    userId: user.id
                }
            });
           
          
            setNotebooks(response.data)
            

        }catch ( error){
            console.log("Error", error)
        }

    }

    const handleDeleteUser = async(user) => {

        setViewUser(user);
        
        try {
            await axios.post(`http://localhost:8080/user/delete/${user.id}`)
            fetchUsers();
           
            
        }catch(error){
            console.log("Error", error)
        }
        console.log(users)
    }

    return (
        <>
        <UserContext.Provider value={{viewUser,notebook, setNotebooks}}>
        <BasicNav />
        <Container className="mt-5">
           
            <h1 className="text-center mb-5">Welcome to Admin Area!</h1>

            {loadState === false ? (
                <Row>
                    {users.map(user => (
                        <Col key={user.id} md={4} className="mb-4">
                            <Card className="shadow-sm">
                                <Card.Body>
                                    <Card.Title>{user.firstName} {user.lastName} {user.gender}</Card.Title>
                                    <Card.Text><strong>Id:</strong> {user.id}</Card.Text>
                                    <Card.Text><strong>Email:</strong> {user.email}</Card.Text>
                                    <Card.Text><strong>Password:</strong> {user.password}</Card.Text>
                                    <Button variant="primary" className="me-5" onClick={ () => handleViewProfile(user) }>View Profile</Button>
                                    <Button variant="danger" className= "me-5" onClick={ () => handleDeleteUser(user) }> Delete</Button>
                                    <Button variant="warning" >Update</Button>
                                </Card.Body>
                            </Card>
                        </Col>
                    ))}
                </Row>
            ) : (
                <div className="text-center">
                    <Spinner animation="border" variant="primary" />
                    <p>Loading users...</p>
                </div>
            )}
            <UserViewModal show = {infoModal} onHide= {() => setInfoModal(false)}/>
        </Container>
        </UserContext.Provider>
    </>
    );

}

export default DashBoard;
