import React, { useEffect, useState } from 'react';
import axios from 'axios';

function ChatApp() {
  const [groups, setGroups] = useState([]);
  const [selectedGroupId, setSelectedGroupId] = useState('');
  const [messages, setMessages] = useState([]);
  const [messageText, setMessageText] = useState('');
  const [sender, setSender] = useState('');

  const BASE_URL = 'http://localhost:8080';

  useEffect(() => {
    fetchGroups();
  }, []);

  const fetchGroups = async () => {
    try {
      const response = await axios.get(`${BASE_URL}/group`);
      setGroups(response.data);
    } catch (error) {
      console.error('Error fetching groups:', error);
    }
  };

  const fetchMessages = async () => {
    if (!selectedGroupId) return;
    try {
      const response = await axios.get(`${BASE_URL}/message/${selectedGroupId}`);
      setMessages(response.data);
    } catch (error) {
      console.error('Error fetching messages:', error);
    }
  };

  const handleSendMessage = async () => {
    if (!messageText || !sender || !selectedGroupId) return;

    const newMessage = {
      text: messageText,
      sender: sender
    };

    try {
      await axios.post(`${BASE_URL}/message/post?groupChatId=${selectedGroupId}`, newMessage);
      setMessageText('');
      fetchMessages(); // Refresh messages
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="mb-4">Group Chat App</h2>

      <div className="mb-3">
        <label className="form-label">Select Group</label>
        <select
          className="form-select"
          value={selectedGroupId}
          onChange={(e) => {
            setSelectedGroupId(e.target.value);
            setMessages([]);
          }}
        >
          <option value="">-- Choose a Group --</option>
          {groups.map(group => (
            <option key={group.id} value={group.id}>
              {group.name}
            </option>
          ))}
        </select>
        <button className="btn btn-primary mt-2" onClick={fetchMessages} disabled={!selectedGroupId}>
          Load Messages
        </button>
      </div>

      <div className="border p-3 mb-3" style={{ height: '300px', overflowY: 'auto' }}>
        {messages.length === 0 && <p>No messages yet.</p>}
        {messages.map((msg, index) => (
          <div key={index} className="mb-2">
            <strong>{msg.sender}</strong>: {msg.text}
          </div>
        ))}
      </div>

      <div className="mb-3">
        <input
          type="text"
          className="form-control mb-2"
          placeholder="Your name"
          value={sender}
          onChange={(e) => setSender(e.target.value)}
        />
        <input
          type="text"
          className="form-control mb-2"
          placeholder="Enter message"
          value={messageText}
          onChange={(e) => setMessageText(e.target.value)}
        />
        <button
          className="btn btn-success"
          onClick={handleSendMessage}
          disabled={!messageText || !sender || !selectedGroupId}
        >
          Send Message
        </button>
      </div>
    </div>
  );
}

export default ChatApp;
