import React, { useContext } from "react";
import { Navbar, Container, Nav } from "react-bootstrap";
import "../styles/Navbar.css"; // Import the CSS for navbar and gradient overlay
import { LoggedUserContext } from "../context/LoggedUserContext";

function NavBar() {
  const {loggedUser} = useContext(LoggedUserContext);
  return (
    <Navbar expand="lg" className="px-3 navbar-container" sticky = "top">
      {/* Add gradient overlay */}
      <div id="gradient"></div> {/* Gradient overlay */}
      <Container fluid>
        <Navbar.Brand href="/" className="d-flex align-items-center gap-2">
          <img
            alt=""
            src="/icon.png"
            width="50"
            height="50"
            className="d-inline-block align-top"
          />
          Planify
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link href="/login">Logout</Nav.Link>
            <Nav.Link href="/adminBoard">Dash Board</Nav.Link>
          </Nav>

          <Nav className="ms-auto">
            {loggedUser ? (
              <Nav.Item className="navbar-text">
                Welcome, <strong>{loggedUser.lastName} {loggedUser.firstName}</strong>
              </Nav.Item>
            ) : (
              <span className="text-warning">Not Logged In</span>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default NavBar;
