import React, { useContext, useEffect, useState } from "react";
import { LoggedUserContext } from "../../context/LoggedUserContext";
import axios from "axios";
import { Card, Container, Spinner, Button } from "react-bootstrap";
import BasicNav from "../../components/Navbar";
import EditNotebookModal from "../../components/modals/updateNotebookModal"; 

function UserNotebooks() {
  const { loggedUser } = useContext(LoggedUserContext);
  const [notebooks, setNotebooks] = useState([]);
  const [loading, setLoading] = useState(true);

  const [showModal, setShowModal] = useState(false);
  const [currentNotebook, setCurrentNotebook] = useState(null);
  const [updatedNotebook, setUpdatedNotebook] = useState({
    notebookName: "",
    notes: "",
    resolution: "",
  });

  useEffect(() => {
    const fetchNotebooks = async () => {
      if (!loggedUser) return;

      try {
        const response = await axios.get("http://localhost:8080/notebooks", {
          params: { userId: loggedUser.id },
        });
        setNotebooks(response.data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching notebooks:", error);
        setLoading(false);
      }
    };

    fetchNotebooks();
  }, [loggedUser]);

  const handleShowModal = (notebook) => {
    setCurrentNotebook(notebook);
    setUpdatedNotebook({
      notebookName: notebook.notebookName,
      notes: notebook.notes,
      resolution: notebook.resolution,
    });
    setShowModal(true);
  };

  const handleUpdateNotebook = async () => {
    try {
      await axios.post(`http://localhost:8080/notebooks/update`, {
        ...currentNotebook,
        ...updatedNotebook,
        type: "mynotebook",
      });
  
      setNotebooks((prev) =>
        prev.map((nb) =>
          nb.id === currentNotebook.id ? { ...nb, ...updatedNotebook } : nb
        )
      );
  
      setShowModal(false);
    } catch (error) {
      console.error("Error updating notebook:", error);
    }
  };

  return (
    <>
      <BasicNav />
      <Container className="mt-4">
        <h3>Your Notebooks</h3>

        {loading ? (
          <div className="text-center">
            <Spinner animation="border" />
          </div>
        ) : notebooks.length === 0 ? (
          <p className="text-muted">You have no notebooks yet.</p>
        ) : (
          notebooks.map((nb) => (
            <Card key={nb.id} className="mb-3 shadow-sm">
              <Card.Body>
                <Card.Title>Notebook #{nb.id}</Card.Title>
                <Card.Text><strong>Title:</strong> {nb.notebookName}</Card.Text>
                <Card.Text><strong>Notes:</strong> {nb.notes}</Card.Text>
                <Card.Text><strong>Resolution:</strong> {nb.resolution}</Card.Text>
                <Button variant="warning" onClick={() => handleShowModal(nb)}>
                  Update
                </Button>
              </Card.Body>
            </Card>
          ))
        )}
        <Button variant="primary" className="mb-3" onClick={() => setShowCreateModal(true)}>
  + Create Notebook
        </Button>
      </Container>

      <EditNotebookModal
        show={showModal}
        handleClose={() => setShowModal(false)}
        notebookData={updatedNotebook}
        setNotebookData={setUpdatedNotebook}
        handleSave={handleUpdateNotebook}
      />
    </>
  );
}

export default UserNotebooks;
