import React, { useContext, useEffect, useState, useRef, useCallback } from 'react';
import { Button, Form, Dropdown, ListGroup, Container, Row, Col, Alert } from 'react-bootstrap';
import axios from 'axios';
import SockJS from 'sockjs-client';
import { Stomp } from '@stomp/stompjs';
import NavBar from "../../components/Navbar";
import { LoggedUserContext } from '../../context/LoggedUserContext';
import "../../styles/ChatGroup.css";

function GroupChatApp() {
    const [groups, setGroups] = useState([]);
    const [selectedGroup, setSelectedGroup] = useState('');
    const [messages, setMessages] = useState([]);
    const [inputValue, setInputValue] = useState('');
    const [notifications, setNotifications] = useState([]);
    const [unreadNotifications, setUnreadNotifications] = useState(0);
    const { loggedUser } = useContext(LoggedUserContext);
    const stompClient = useRef(null);
    const [isConnected, setIsConnected] = useState(false);
    const currentGroupSubscription = useRef(null); // To manage group-specific subscription

    // --- Helper Functions (Ensuring they are defined in scope) ---

    // Fetch groups function
    const fetchGroups = async () => {
        try {
            const response = await axios.get('http://localhost:8080/group');
            setGroups(response.data);
        } catch (err) {
            console.error('GET /group failed:', err);
        }
    };

    // Remove notification (using useCallback for optimization)
    const removeNotification = useCallback((notificationId) => {
        setNotifications(prev => {
            const notification = prev.find(n => n.id === notificationId);
            if (notification && !notification.read) {
                setUnreadNotifications(count => Math.max(0, count - 1));
            }
            return prev.filter(n => n.id !== notificationId);
        });
    }, []); // No dependencies as it operates on internal state setters

    // Mark notification as read (using useCallback for optimization)
    const markNotificationAsRead = useCallback((notificationId) => {
        setNotifications(prev => prev.map(n => {
            if (n.id === notificationId && !n.read) {
                setUnreadNotifications(count => Math.max(0, count - 1));
                return { ...n, read: true };
            }
            return n;
        }));
    }, []); // No dependencies as it operates on internal state setters

    // Switch to group from notification (using useCallback for optimization)
    const switchToGroupFromNotification = useCallback((groupName, notificationId) => {
        setSelectedGroup(groupName);
        markNotificationAsRead(notificationId);
    }, [markNotificationAsRead]); // Depends on markNotificationAsRead

    // Helper function to display message text reliably
    const getMessageText = useCallback((msg) => {
        return msg.content || msg.text || msg.message || '[No message]';
    }, []);

    // Helper function to format timestamp
    const formatTimestamp = useCallback((timestamp) => {
        if (!timestamp) return '';

        const date = new Date(timestamp);
        const now = new Date();
        const isToday = date.toDateString() === now.toDateString();

        if (isToday) {
            return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
        } else {
            return date.toLocaleString([], {
                month: 'short',
                day: 'numeric',
                hour: '2-digit',
                minute: '2-digit'
            });
        }
    }, []);

    // Request Notification Permission (for desktop notifications)
    const requestDesktopNotificationPermission = () => {
        if (!("Notification" in window)) {
            console.warn("This browser does not support desktop notification");
        } else if (Notification.permission !== 'granted' && Notification.permission !== 'denied') {
            Notification.requestPermission().then(permission => {
                if (permission === "granted") {
                    console.log("Desktop notification permission granted.");
                } else {
                    console.warn("Desktop notification permission denied.");
                }
            });
        }
    };


    // --- WebSocket Connection Setup (Runs once on mount) ---
    useEffect(() => {
        const socket = new SockJS('http://localhost:8080/socket');
        stompClient.current = Stomp.over(socket);

        stompClient.current.connect({}, (frame) => {
            console.log('Connected to WebSocket:', frame);
            setIsConnected(true);

            // Subscribe to GLOBAL notifications
            stompClient.current.subscribe('/topic/notifications', (message) => {
                const notification = JSON.parse(message.body);
                handleWebSocketNotification(notification);
            });
        }, (error) => {
            console.error('WebSocket connection error:', error);
            setIsConnected(false);
            // In a real app, you might want to implement a reconnect strategy here
        });

        // Cleanup: Disconnect WebSocket when component unmounts
        return () => {
            if (stompClient.current && stompClient.current.connected) {
                stompClient.current.disconnect(() => {
                    console.log("WebSocket disconnected.");
                });
            }
        };
    }, []); // Empty dependency array means this effect runs only once on mount


    // --- Handle Incoming WebSocket Notifications (Callback for reusability) ---
    const handleWebSocketNotification = useCallback((notification) => {
        if (notification.type === 'NEW_MESSAGE') {
            const currentUserFullName = loggedUser ? `${loggedUser.firstName} ${loggedUser.lastName}` : null;

            // Don't show notification for your own messages
            if (currentUserFullName && notification.sender === currentUserFullName) {
                return;
            }

            // Get the ID of the currently selected group
            const currentSelectedGroupObject = groups.find(g => g.name === selectedGroup);
            const currentSelectedGroupId = currentSelectedGroupObject ? currentSelectedGroupObject.id : null;

            // If user is NOT viewing this group, process as a notification
            if (!currentSelectedGroupId || currentSelectedGroupId !== notification.groupId) {
                const newNotification = {
                    id: Date.now(), // Unique ID for React key and dismissal
                    type: 'message',
                    groupName: notification.groupName,
                    groupId: notification.groupId,
                    sender: notification.sender,
                    message: notification.message,
                    timestamp: new Date(notification.timestamp),
                    read: false
                };

                setNotifications(prev => [newNotification, ...prev].slice(0, 10)); // Keep only 10 latest
                setUnreadNotifications(prev => prev + 1);

                // Auto-remove app-level notification after 10 seconds
                setTimeout(() => {
                    removeNotification(newNotification.id);
                }, 10000);

                // --- Trigger Desktop Notification (Optional) ---
                if ("Notification" in window && Notification.permission === "granted") {
                    const title = `New Message in ${notification.groupName} from ${notification.sender}`;
                    const options = {
                        body: notification.message,
                        icon: '/path/to/your/chat-icon.png', // Path to a small icon for the notification
                        tag: `group-chat-${notification.groupId}`, // Groups notifications for the same chat
                        // vibrate: [200, 100, 200], // Optional: for mobile
                    };
                    const desktopNotif = new Notification(title, options);

                    desktopNotif.onclick = function() {
                        window.focus(); // Bring the browser tab to focus
                        // You can also programmatically switch the selected group if the user clicks the notification
                        if (newNotification.groupName) {
                            setSelectedGroup(newNotification.groupName);
                        }
                        markNotificationAsRead(newNotification.id); // Mark the in-app notification as read
                        desktopNotif.close(); // Close the desktop notification
                    };
                }
            }
        }
    }, [loggedUser, selectedGroup, groups, removeNotification, markNotificationAsRead]); // Dependencies for useCallback


    // --- Fetch Groups on Component Mount ---
    useEffect(() => {
        fetchGroups();
    }, []);


    // --- Effect for Handling Group Selection and Subscribing to Group-Specific Messages ---
    useEffect(() => {
        // Condition to ensure we have a client and are connected before subscribing
        if (!stompClient.current || !isConnected) {
            if (currentGroupSubscription.current) {
                currentGroupSubscription.current.unsubscribe();
                currentGroupSubscription.current = null;
            }
            setMessages([]);
            return;
        }

        const group = groups.find(g => g.name === selectedGroup);

        // If no group is selected or the selected group isn't found
        if (!selectedGroup || !group) {
            if (currentGroupSubscription.current) {
                currentGroupSubscription.current.unsubscribe();
                currentGroupSubscription.current = null;
            }
            setMessages([]); // Clear messages when no group is selected
            return;
        }

        // Fetch initial messages for the selected group
        const fetchInitialMessages = async () => {
            try {
                const res = await axios.get(`http://localhost:8080/message/${group.id}`);
                setMessages(res.data);
            } catch (err) {
                console.error('Error fetching initial messages for group:', err);
            }
        };

        fetchInitialMessages();

        // Unsubscribe from previous group's topic if any active subscription exists
        if (currentGroupSubscription.current) {
            currentGroupSubscription.current.unsubscribe();
        }

        // Subscribe to the specific group's chat topic for real-time updates
        const subscription = stompClient.current.subscribe(`/topic/group/${group.id}`, (message) => {
            const newMessage = JSON.parse(message.body);
            // Only add if it's truly for the current group (defensive check)
            if (newMessage.group && newMessage.group.id === group.id) {
                setMessages(prevMessages => [...prevMessages, newMessage]);
            }
        });
        currentGroupSubscription.current = subscription; // Store the new subscription object

        // Cleanup function for when the component unmounts or selectedGroup/isConnected changes
        return () => {
            if (currentGroupSubscription.current) {
                currentGroupSubscription.current.unsubscribe();
                currentGroupSubscription.current = null;
            }
        };
    }, [selectedGroup, groups, isConnected]); // Re-run this effect when selectedGroup, groups, or connection status changes


    // --- Handlers for User Actions ---

    const handleSend = async () => {
        if (!inputValue.trim() || !selectedGroup || !stompClient.current || !isConnected) return;

        const group = groups.find(g => g.name === selectedGroup);
        if (!group) {
            console.error("Selected group not found");
            return;
        }

        const messagePayload = {
            sender: `${loggedUser.firstName} ${loggedUser.lastName}`,
            message: inputValue,
            // You might include groupId in the message model on backend if needed for more complex routing
            // but for simplicity, groupChatId is passed as a request param in the current backend
        };

        try {
            // Send message via HTTP POST for persistence
            await axios.post(`http://localhost:8080/message/post?groupChatId=${group.id}`, messagePayload);

            // Clear input only after successful send
            setInputValue('');
            // The new message will be pushed via WebSocket to `/topic/group/{groupId}`
            // and handled by the subscription in the useEffect above, so no need to re-fetch all messages here.
        } catch (err) {
            console.error('Sending message failed:', err);
        }
    };

    const handleCreateGroup = async (e) => {
        e.preventDefault();
        if (!inputValue.trim()) return;

        try {
            await axios.post('http://localhost:8080/group/create', { name: inputValue });

            await fetchGroups(); // Re-fetch groups to update the dropdown
            setSelectedGroup(inputValue); // Automatically select the newly created group
            setInputValue('');
        } catch (err) {
            console.error('Create group error:', err);
        }
    };


    return (
        <div className="group-chat-container">
            <NavBar />
            <Container>
                <div className="chat-main-card">
                    <div className="d-flex justify-content-between align-items-center mb-3">
                        <h3 className="chat-header">ðŸ’¬ Group Chat</h3>
                        <div className="connection-status">
                            {isConnected ? (
                                <span className="text-success">ðŸŸ¢ Connected</span>
                            ) : (
                                <span className="text-danger">ðŸ”´ Disconnected</span>
                            )}
                            <Button
                                variant="outline-primary"
                                size="sm"
                                className="ms-3"
                                onClick={requestDesktopNotificationPermission}
                                title="Enable browser pop-up notifications"
                            >
                                Get Desktop Notifications ðŸ””
                            </Button>
                        </div>
                    </div>

                    {/* Real-time Notifications Bar */}
                    {notifications.length > 0 && (
                        <div className="notifications-bar mb-3 p-3 border rounded shadow-sm">
                            <div className="d-flex justify-content-between align-items-center mb-2">
                                <h6 className="mb-0">
                                    ðŸ”” Live Notifications
                                    {unreadNotifications > 0 && (
                                        <span className="badge bg-danger ms-2">{unreadNotifications}</span>
                                    )}
                                </h6>
                            </div>
                            <div className="notifications-list">
                                {notifications.slice(0, 3).map((notification) => (
                                    <Alert
                                        key={notification.id}
                                        variant={notification.read ? "secondary" : "info"}
                                        className="notification-alert mb-2"
                                        dismissible
                                        onClose={() => removeNotification(notification.id)}
                                    >
                                        <div className="d-flex justify-content-between align-items-start">
                                            <div
                                                className="notification-content flex-grow-1"
                                                style={{ cursor: 'pointer' }}
                                                onClick={() => switchToGroupFromNotification(notification.groupName, notification.id)}
                                            >
                                                <strong>{notification.sender}</strong> in <strong>{notification.groupName}</strong>
                                                <br />
                                                <small>{notification.message}</small>
                                            </div>
                                            <small className="text-muted ms-2">
                                                {formatTimestamp(notification.timestamp)}
                                            </small>
                                        </div>
                                    </Alert>
                                ))}
                                {notifications.length > 3 && (
                                    <small className="text-muted">
                                        +{notifications.length - 3} more notifications...
                                    </small>
                                )}
                            </div>
                        </div>
                    )}

                    <div className="group-controls mb-4">
                        <Row>
                            <Col md={6} className="mb-3 mb-md-0">
                                <div className="group-dropdown">
                                    <Dropdown onSelect={setSelectedGroup}>
                                        <Dropdown.Toggle className="w-100">
                                            {selectedGroup || 'Select Group'}
                                        </Dropdown.Toggle>
                                        <Dropdown.Menu>
                                            {groups.map((g, i) => (
                                                <Dropdown.Item key={i} eventKey={g.name}>{g.name}</Dropdown.Item>
                                            ))}
                                        </Dropdown.Menu>
                                    </Dropdown>
                                </div>
                            </Col>

                            <Col md={6}>
                                <Form onSubmit={handleCreateGroup} className="create-group-form d-flex">
                                    <Form.Control
                                        type="text"
                                        placeholder="Create new group..."
                                        value={inputValue}
                                        onChange={e => setInputValue(e.target.value)}
                                        className="create-group-input me-2"
                                    />
                                    <Button type="submit" className="create-group-btn">
                                        âœ¨ Create Group
                                    </Button>
                                </Form>
                            </Col>
                        </Row>
                    </div>

                    {selectedGroup ? (
                        <div className="chat-section card p-3">
                            <h5 className="chat-title mb-3">ðŸ“¨ Messages in {selectedGroup}</h5>
                            <ListGroup className="messages-container flex-grow-1 mb-3">
                                {messages.length > 0 ? messages.map((msg, idx) => (
                                    <ListGroup.Item
                                        key={msg.id || idx} // Use msg.id if available for better keying
                                        className={`message-item ${msg.sender === `${loggedUser.firstName} ${loggedUser.lastName}` ? 'my-message' : 'other-message'}`}
                                    >
                                        <div className="message-content">
                                            <div className="message-text">
                                                <span className="message-sender">
                                                    {msg.sender === `${loggedUser.firstName} ${loggedUser.lastName}` ? 'You' : msg.sender || 'Unknown'}
                                                </span>
                                                {getMessageText(msg)}
                                            </div>
                                            <span className="message-timestamp">
                                                {formatTimestamp(msg.timestamp)}
                                            </span>
                                        </div>
                                    </ListGroup.Item>
                                )) : (
                                    <div className="empty-state">
                                        <div className="empty-state-icon">ðŸ’­</div>
                                        <p>No messages yet. Start the conversation!</p>
                                    </div>
                                )}
                            </ListGroup>

                            <Form className="message-input-form d-flex" onSubmit={e => { e.preventDefault(); handleSend(); }}>
                                <Form.Control
                                    type="text"
                                    placeholder="Type your message..."
                                    value={inputValue}
                                    onChange={e => setInputValue(e.target.value)}
                                    className="message-input me-2"
                                />
                                <Button type="submit" className="send-btn">
                                    Send ðŸš€
                                </Button>
                            </Form>
                        </div>
                    ) : (
                        <div className="empty-state card p-4">
                            <div className="empty-state-icon">ðŸ‘†</div>
                            <p>Select a group to start chatting!</p>
                        </div>
                    )}
                </div>
            </Container>
        </div>
    );
}

export default GroupChatApp;
