import React, { useContext, useEffect, useState } from 'react';
import { Button, Form, Dropdown, ListGroup, Container, Row, Col } from 'react-bootstrap';
import axios from 'axios';
import NavBar from "../../components/Navbar";
import { LoggedUserContext } from '../../context/LoggedUserContext';
import '../../styles/ChatGroup.css'
function GroupChatApp() {
    const [groups, setGroups] = useState([]);
    const [selectedGroup, setSelectedGroup] = useState('');
    const [messages, setMessages] = useState([]);
    const [inputValue, setInputValue] = useState('');
    const [notifications, setNotifications] = useState([]);
    const {loggedUser} = useContext(LoggedUserContext);

    // Fetch groups function
    const fetchGroups = async () => {
        try {
            const response = await axios.get('http://localhost:8080/group');
            setGroups(response.data);
        } catch (err) {
            console.error('GET /group failed:', err);
        }
    };

    useEffect(() => {
        fetchGroups();
    }, []);

    // Poll messages every 2 seconds
    useEffect(() => {
        if (!selectedGroup) return;

        const group = groups.find(g => g.name === selectedGroup);
        if (!group) return;

        const fetchMessages = async () => {
            try {
                const res = await axios.get(`http://localhost:8080/message/${group.id}`);
                setMessages(res.data);
            } catch (err) {
                console.error('Polling error:', err);
            }
        };

        fetchMessages(); // Initial fetch

        const interval = setInterval(fetchMessages, 2000); // Changed to 2 seconds for better performance

        return () => clearInterval(interval); // Cleanup
    }, [selectedGroup, groups]);

    const handleSend = async () => {
        if (!inputValue.trim() || !selectedGroup) return;

        const group = groups.find(g => g.name === selectedGroup);
        if (!group) {
            console.error("Selected group not found");
            return;
        }

        // Adjust payload property name to match backend 'content' or 'text'
        const messagePayload = {
            sender: `${loggedUser.firstName+" "+loggedUser.lastName}`,
            message: inputValue
        };

        try {
            console.log(`${loggedUser.firstName}`)
            await axios.post(`http://localhost:8080/message/post?groupChatId=${group.id}`, messagePayload);

            const response = await axios.get(`http://localhost:8080/message/${group.id}`);
            setMessages(response.data);
            setInputValue('');
        } catch (err) {
            console.error('Sending message failed:', err);
        }
    };

    const handleCreateGroup = async (e) => {
        e.preventDefault();
        if (!inputValue.trim()) return;

        try {
            // Create the group and get the response with the actual group data
            const response = await axios.post('http://localhost:8080/group/create', { name: inputValue });
            
            // Refresh the groups list to get the latest data with correct IDs
            await fetchGroups();
            
            // Set the selected group to the newly created one
            setSelectedGroup(inputValue);
            setInputValue('');
            
            // Add success notification
            setNotifications(prev => [...prev, `Group "${inputValue}" created successfully!`]);
        } catch (err) {
            console.error('Create group error:', err);
            setNotifications(prev => [...prev, `Failed to create group: ${err.message}`]);
        }
    };

    // Helper function to display message text reliably
    const getMessageText = (msg) => {
        return msg.content || msg.text || msg.message || '[No message]';
    };

    // Helper function to format timestamp
    const formatTimestamp = (timestamp) => {
        if (!timestamp) return '';
        
        const date = new Date(timestamp);
        const now = new Date();
        const isToday = date.toDateString() === now.toDateString();
        
        if (isToday) {
            // Show only time if message is from today
            return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
        } else {
            // Show date and time if message is from another day
            return date.toLocaleString([], { 
                month: 'short', 
                day: 'numeric', 
                hour: '2-digit', 
                minute: '2-digit' 
            });
        }
    };

    return (
        <div>
            <NavBar />
            <Container className="mt-4">
                <h3>Group Chat App</h3>

                <Row className="mb-3">
                    <Col md={6}>
                        <Dropdown onSelect={setSelectedGroup}>
                            <Dropdown.Toggle variant="primary">
                                {selectedGroup || 'Select Group'}
                            </Dropdown.Toggle>
                            <Dropdown.Menu>
                                {groups.map((g, i) => (
                                    <Dropdown.Item key={i} eventKey={g.name}>{g.name}</Dropdown.Item>
                                ))}
                            </Dropdown.Menu>
                        </Dropdown>
                    </Col>

                    <Col md={6}>
                        <Form onSubmit={handleCreateGroup}>
                            <Form.Control
                                type="text"
                                placeholder="Create new group"
                                value={inputValue}
                                onChange={e => setInputValue(e.target.value)}
                            />
                            <Button type="submit" className="mt-2">Create Group</Button>
                        </Form>
                    </Col>
                </Row>

                {selectedGroup && (
                    <>
                        <h5>Messages in {selectedGroup}</h5>
                        <ListGroup className="mb-3" style={{ maxHeight: '300px', overflowY: 'auto' }}>
                            {messages.map((msg, idx) => (
                                <ListGroup.Item key={idx}>
                                    <div className="d-flex justify-content-between align-items-start">
                                        <div>
                                            <strong>{msg.sender || 'Unknown'}:</strong> {getMessageText(msg)}
                                        </div>
                                        <small className="text-muted ms-2" style={{ minWidth: 'max-content' }}>
                                            {formatTimestamp(msg.timestamp)}
                                        </small>
                                    </div>
                                </ListGroup.Item>
                            ))}
                        </ListGroup>

                        <Form className="d-flex mb-3" onSubmit={e => { e.preventDefault(); handleSend(); }}>
                            <Form.Control
                                type="text"
                                placeholder="Enter message"
                                value={inputValue}
                                onChange={e => setInputValue(e.target.value)}
                            />
                            <Button type="submit" className="ms-2">Send</Button>
                        </Form>
                    </>
                )}

                <h5>Notifications</h5>
                <ListGroup>
                    {notifications.map((note, i) => (
                        <ListGroup.Item key={i}>{note}</ListGroup.Item>
                    ))}
                </ListGroup>
            </Container>
        </div>
    );
}

export default GroupChatApp;
