import { useContext } from 'react';
import { UserContext } from '../../pages/Home/AdminBoard';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Card from 'react-bootstrap/Card';
import Stack from 'react-bootstrap/Stack';
import { useSwipeable } from 'react-swipeable'; // Using react-swipeable for gestures

import "../../styles/NotebookView.css"
import axios from 'axios';

function UserViewModal(props) {
  const { viewUser, notebook, setNotebooks } = useContext(UserContext);

  const handleDeleteNotebook = async (nb) => {
    try {
      const response = await axios.post("http://localhost:8080/notebooks/delete", nb);
      setNotebooks(response.data);
    } catch (error) {
      console.log("Error:", error);
    }
  };

  // Swipe handler without animation
  const swipeHandlers = (nb) =>
    useSwipeable({
      onSwipedLeft: () => handleDeleteNotebook(nb),
      preventDefaultTouchmoveEvent: true,
      trackMouse: true,
    });

  if (!viewUser) {
    return null;
  }

  return (
    <Modal
      {...props}
      size="lg"
      aria-labelledby="contained-modal-title-vcenter"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          User Info
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <h5 className="mb-4">User's Notebooks</h5>
        {notebook.length > 0 ? (
          <Stack gap={3}>
            {notebook.map((nb) => (
              <div key={nb.id} {...swipeHandlers(nb)}>
                <Card className="notebook-entry shadow-sm">
                  <Card.Body>
                    <Card.Title className="text-primary">Notebook #{nb.id}</Card.Title>
                    <Card.Text><strong>Title:</strong> {nb.notebookName}</Card.Text>
                    <Card.Text><strong>Notes:</strong> {nb.notes}</Card.Text>
                    <Card.Text><strong>Resolution:</strong> {nb.resolution}</Card.Text>
                  </Card.Body>
                </Card>
              </div>
            ))}
          </Stack>
        ) : (
          <p className="text-muted">No notebooks available for this user.</p>
        )}
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={props.onHide}>Close</Button>
      </Modal.Footer>
    </Modal>
  );
}

export default UserViewModal;
