import React, { useContext } from "react";
import { Navbar, Container, Nav } from "react-bootstrap";
import "../styles/Navbar.css"; // Import the CSS for navbar and gradient overlay
import { LoggedUserContext } from "../context/LoggedUserContext";
import { useNavigate } from "react-router-dom";

function NavBar() {
  const { loggedUser, setLoggedUser } = useContext(LoggedUserContext);
  const nagivate = useNavigate();
  const handleLogout = () => {

    localStorage.removeItem("loggedUser");
    setLoggedUser(null);
    nagivate("/login");
    }
  return (
    <Navbar expand="lg" className="px-3 navbar-container" sticky="top">
      
      <div id="gradient"></div> 
      <Container fluid>
        <Navbar.Brand href="/" className="d-flex align-items-center gap-2">
          <img
            alt=""
            src="/icon.png"
            width="50"
            height="50"
            className="d-inline-block align-top"
          />
          Planify
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link onClick={handleLogout}>Logout</Nav.Link>
              
            {loggedUser && (
              <Nav.Link href="/notebooks">Notebooks</Nav.Link>
            )}
          </Nav>

          {loggedUser.role === 'admin' &&
            <Nav.Link href="/adminBoard">Dashboard</Nav.Link>
            }  

          <Nav className="ms-auto">
            {loggedUser ? (
              <Nav.Item className="navbar-text">
                Welcome, <strong>{loggedUser.lastName} {loggedUser.firstName}</strong>
              </Nav.Item>
            ) : (
              <span className="navbar-text">Guest Mode</span>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default NavBar;
