import React, { useEffect, useState } from 'react';
import SockJS from 'sockjs-client';
import { Client } from '@stomp/stompjs';
import axios from 'axios';

function WebSocketComponent() {
    const [message, setMessage] = useState('');
    const [inputValue, setInputValue] = useState('');
    const [stompClient, setStompClient] = useState(null);

    useEffect(() => {
        const socket = new SockJS('http://localhost:8080/socket');
        const client = new Client({
            webSocketFactory: () => socket,
            debug: (str) => console.log(str),
            onConnect: () => {
                console.log('Connected to WebSocket');

                // Subscribe to the shared topic
                client.subscribe('/topic/message', (msg) => {
                    console.log('Received message:', msg.body);
                    setMessage(msg.body);
                });
            },
            onDisconnect: () => {
                console.log('Disconnected');
            },
        });

        client.activate();
        setStompClient(client);

        return () => {
            client.deactivate();
        };
    }, []);

    const sendPing = () => {
        if (inputValue.trim() === '') {
            alert('Please enter a message to send.');
            return;
        }

        axios.post('http://localhost:8080/user/message', {
            message: inputValue
        })
        .then((response) => {
            console.log('Message sent successfully', response.data);
            setInputValue(''); // clear the input after sending
        })
        .catch((error) => {
            console.error('Error sending message:', error);
        });
    };

    return (
        <div>
            <h3>WebSocket Group Chat:</h3>
            <p><strong>Last message:</strong> {message}</p>

            <div style={{ marginTop: '20px' }}>
                <input
                    type="text"
                    placeholder="Enter your message"
                    value={inputValue}
                    onChange={(e) => setInputValue(e.target.value)}
                    style={{ padding: '8px', width: '300px', marginRight: '10px' }}
                />
                <button onClick={sendPing} style={{ padding: '8px 16px' }}>
                    Send Message
                </button>
            </div>
        </div>
    );
}

export default WebSocketComponent;
