import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import '../styles/Navbar.css'
import { useEffect } from 'react';
// import NavDropdown from 'react-bootstrap/NavDropdown';
// import {FaBook, FaEdit} from 'react-icons/fa';
function BasicNav() {

  const [gradientIndex, setGradientIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setGradientIndex((prevIndex) => (prevIndex + 1) % 3); // Change gradient every 5 seconds
    }, 5000); // Update every 5 seconds

    return () => clearInterval(interval); // Cleanup on component unmount
  }, []);

  const gradients = [
    "linear-gradient(135deg, #74ebd5, #ACB6E5)",
    "linear-gradient(135deg, #ACB6E5, #0eee98)",
    "linear-gradient(135deg, #0eee98, #74ebd5)"
  ];
  return (
    <Navbar style={{ background: gradients[gradientIndex], transition: "background 1s ease" }}>
  <Container fluid>
    <Navbar.Brand href="/" className="d-flex align-items-center gap-2">
      <img
        alt=""
        src="/icon.png"
        width="50"
        height="50"
        className="d-inline-block align-top"
      />
      Planify
    </Navbar.Brand>
    <Navbar.Toggle aria-controls="basic-navbar-nav" />
    <Navbar.Collapse id="basic-navbar-nav">
      <Nav className="me-auto">
        <Nav.Link href="/login">Logout</Nav.Link>
      </Nav>
    </Navbar.Collapse>
  </Container>
</Navbar>

  );
}

export default BasicNav;
