import React, { useEffect, useState } from 'react';
import SockJS from 'sockjs-client';
import { Client } from '@stomp/stompjs';
import axios from 'axios';

function WebSocketComponent() {
    const [message, setMessage] = useState('');
    const [stompClient, setStompClient] = useState(null);

    useEffect(() => {
        const socket = new SockJS('http://localhost:8080/socket');
        const client = new Client({
            webSocketFactory: () => socket,
            debug: (str) => console.log(str),
            onConnect: () => {
                console.log('Connected to WebSocket');

                // Subscribe to the shared topic for all users
                client.subscribe('/topic/message', (msg) => {
                    console.log('Received message:', msg.body);
                    setMessage(msg.body);
                });
            },
            onDisconnect: () => {
                console.log('Disconnected');
            },
        });

        client.activate();
        setStompClient(client);

        return () => {
            client.deactivate();
        };
    }, []);

    const sendPing = () => {
        axios.post('http://localhost:8080/user/message', {
            message: 'Ping to all users!'
        })
        .then((response) => {
            console.log('Ping sent to all users successfully', response.data);
        })
        .catch((error) => {
            console.error('Error sending ping:', error);
        });
    };

    return (
        <div>
            <h3>WebSocket Group Message:</h3>
            <p>{message}</p>
            <button onClick={sendPing} className="btn btn-primary">
                Ping all users
            </button>
        </div>
    );
}

export default WebSocketComponent;
