import React, { useContext, useEffect, useState, useRef } from 'react';
import { Button, Form, Dropdown, ListGroup, Container, Row, Col, Alert } from 'react-bootstrap';
import axios from 'axios';
import SockJS from 'sockjs-client';
import { Stomp } from '@stomp/stompjs';
import NavBar from "../../components/Navbar";
import { LoggedUserContext } from '../../context/LoggedUserContext';
import "../../styles/"

function GroupChatApp() {
    const [groups, setGroups] = useState([]);
    const [selectedGroup, setSelectedGroup] = useState('');
    const [messages, setMessages] = useState([]);
    const [inputValue, setInputValue] = useState('');
    const [notifications, setNotifications] = useState([]);
    const [unreadNotifications, setUnreadNotifications] = useState(0);
    const {loggedUser} = useContext(LoggedUserContext);
    const stompClient = useRef(null);
    const [isConnected, setIsConnected] = useState(false);

    // Fetch groups function
    const fetchGroups = async () => {
        try {
            const response = await axios.get('http://localhost:8080/group');
            setGroups(response.data);
        } catch (err) {
            console.error('GET /group failed:', err);
        }
    };

    // WebSocket connection setup
    useEffect(() => {
        const socket = new SockJS('http://localhost:8080/socket');
        stompClient.current = Stomp.over(socket);
        
        stompClient.current.connect({}, (frame) => {
            console.log('Connected to WebSocket:', frame);
            setIsConnected(true);
            
            // Subscribe to notifications
            stompClient.current.subscribe('/topic/notifications', (message) => {
                const notification = JSON.parse(message.body);
                handleWebSocketNotification(notification);
            });
        }, (error) => {
            console.error('WebSocket connection error:', error);
            setIsConnected(false);
        });

        return () => {
            if (stompClient.current) {
                stompClient.current.disconnect();
            }
        };
    }, []);

    // Handle incoming WebSocket notifications
    const handleWebSocketNotification = (notification) => {
        if (notification.type === 'NEW_MESSAGE') {
            const currentUser = `${loggedUser.firstName} ${loggedUser.lastName}`;
            
            // Don't show notification for your own messages
            if (notification.sender === currentUser) {
                return;
            }
            
            // If user is not viewing this group, show notification
            if (!selectedGroup || selectedGroup !== notification.groupName) {
                const newNotification = {
                    id: Date.now(),
                    type: 'message',
                    groupName: notification.groupName,
                    groupId: notification.groupId,
                    sender: notification.sender,
                    message: notification.message,
                    timestamp: new Date(notification.timestamp),
                    read: false
                };
                
                setNotifications(prev => [newNotification, ...prev].slice(0, 10)); // Keep only 10 latest
                setUnreadNotifications(prev => prev + 1);
                
                // Auto-remove notification after 10 seconds
                setTimeout(() => {
                    removeNotification(newNotification.id);
                }, 10000);
            }
        }
    };

    // Remove notification
    const removeNotification = (notificationId) => {
        setNotifications(prev => {
            const notification = prev.find(n => n.id === notificationId);
            if (notification && !notification.read) {
                setUnreadNotifications(count => Math.max(0, count - 1));
            }
            return prev.filter(n => n.id !== notificationId);
        });
    };

    // Mark notification as read
    const markNotificationAsRead = (notificationId) => {
        setNotifications(prev => prev.map(n => {
            if (n.id === notificationId && !n.read) {
                setUnreadNotifications(count => Math.max(0, count - 1));
                return { ...n, read: true };
            }
            return n;
        }));
    };

    // Switch to group from notification
    const switchToGroupFromNotification = (groupName, notificationId) => {
        setSelectedGroup(groupName);
        markNotificationAsRead(notificationId);
    };

    useEffect(() => {
        fetchGroups();
    }, []);

    // Poll messages every 2 seconds
    useEffect(() => {
        if (!selectedGroup) return;

        const group = groups.find(g => g.name === selectedGroup);
        if (!group) return;

        const fetchMessages = async () => {
            try {
                const res = await axios.get(`http://localhost:8080/message/${group.id}`);
                setMessages(res.data);
            } catch (err) {
                console.error('Polling error:', err);
            }
        };

        fetchMessages(); // Initial fetch

        const interval = setInterval(fetchMessages, 2000);

        return () => clearInterval(interval); // Cleanup
    }, [selectedGroup, groups]);

    const handleSend = async () => {
        if (!inputValue.trim() || !selectedGroup) return;

        const group = groups.find(g => g.name === selectedGroup);
        if (!group) {
            console.error("Selected group not found");
            return;
        }

        const messagePayload = {
            sender: `${loggedUser.firstName} ${loggedUser.lastName}`,
            message: inputValue
        };

        try {
            await axios.post(`http://localhost:8080/message/post?groupChatId=${group.id}`, messagePayload);

            const response = await axios.get(`http://localhost:8080/message/${group.id}`);
            setMessages(response.data);
            setInputValue('');
        } catch (err) {
            console.error('Sending message failed:', err);
        }
    };

    const handleCreateGroup = async (e) => {
        e.preventDefault();
        if (!inputValue.trim()) return;

        try {
            await axios.post('http://localhost:8080/group/create', { name: inputValue });
            
            await fetchGroups();
            setSelectedGroup(inputValue);
            setInputValue('');
        } catch (err) {
            console.error('Create group error:', err);
        }
    };

    // Helper function to display message text reliably
    const getMessageText = (msg) => {
        return msg.content || msg.text || msg.message || '[No message]';
    };

    // Helper function to format timestamp
    const formatTimestamp = (timestamp) => {
        if (!timestamp) return '';
        
        const date = new Date(timestamp);
        const now = new Date();
        const isToday = date.toDateString() === now.toDateString();
        
        if (isToday) {
            return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
        } else {
            return date.toLocaleString([], { 
                month: 'short', 
                day: 'numeric', 
                hour: '2-digit', 
                minute: '2-digit' 
            });
        }
    };

    return (
        <div className="group-chat-container">
            <NavBar />
            <Container>
                <div className="chat-main-card">
                    <div className="d-flex justify-content-between align-items-center">
                        <h3 className="chat-header">ðŸ’¬ Group Chat</h3>
                        <div className="connection-status">
                            {isConnected ? (
                                <span className="text-success">ðŸŸ¢ Connected</span>
                            ) : (
                                <span className="text-danger">ðŸ”´ Disconnected</span>
                            )}
                        </div>
                    </div>

                    {/* Real-time Notifications Bar */}
                    {notifications.length > 0 && (
                        <div className="notifications-bar mb-3">
                            <div className="d-flex justify-content-between align-items-center mb-2">
                                <h6 className="mb-0">
                                    ðŸ”” Live Notifications 
                                    {unreadNotifications > 0 && (
                                        <span className="badge bg-danger ms-2">{unreadNotifications}</span>
                                    )}
                                </h6>
                            </div>
                            <div className="notifications-list">
                                {notifications.slice(0, 3).map((notification) => (
                                    <Alert 
                                        key={notification.id}
                                        variant={notification.read ? "secondary" : "info"}
                                        className="notification-alert mb-2"
                                        dismissible
                                        onClose={() => removeNotification(notification.id)}
                                    >
                                        <div className="d-flex justify-content-between align-items-start">
                                            <div 
                                                className="notification-content flex-grow-1"
                                                style={{ cursor: 'pointer' }}
                                                onClick={() => switchToGroupFromNotification(notification.groupName, notification.id)}
                                            >
                                                <strong>{notification.sender}</strong> in <strong>{notification.groupName}</strong>
                                                <br />
                                                <small>{notification.message}</small>
                                            </div>
                                            <small className="text-muted ms-2">
                                                {formatTimestamp(notification.timestamp)}
                                            </small>
                                        </div>
                                    </Alert>
                                ))}
                                {notifications.length > 3 && (
                                    <small className="text-muted">
                                        +{notifications.length - 3} more notifications...
                                    </small>
                                )}
                            </div>
                        </div>
                    )}

                    <div className="group-controls">
                        <Row>
                            <Col md={6}>
                                <div className="group-dropdown">
                                    <Dropdown onSelect={setSelectedGroup}>
                                        <Dropdown.Toggle className="w-100">
                                            {selectedGroup || 'Select Group'}
                                        </Dropdown.Toggle>
                                        <Dropdown.Menu>
                                            {groups.map((g, i) => (
                                                <Dropdown.Item key={i} eventKey={g.name}>{g.name}</Dropdown.Item>
                                            ))}
                                        </Dropdown.Menu>
                                    </Dropdown>
                                </div>
                            </Col>

                            <Col md={6}>
                                <Form onSubmit={handleCreateGroup} className="create-group-form">
                                    <Form.Control
                                        type="text"
                                        placeholder="Create new group..."
                                        value={inputValue}
                                        onChange={e => setInputValue(e.target.value)}
                                        className="create-group-input"
                                    />
                                    <Button type="submit" className="create-group-btn">
                                        âœ¨ Create Group
                                    </Button>
                                </Form>
                            </Col>
                        </Row>
                    </div>

                    {selectedGroup ? (
                        <div className="chat-section">
                            <h5 className="chat-title">ðŸ“¨ Messages in {selectedGroup}</h5>
                            <ListGroup className="messages-container">
                                {messages.length > 0 ? messages.map((msg, idx) => (
                                    <ListGroup.Item key={idx} className="message-item">
                                        <div className="message-content">
                                            <div className="message-text">
                                                <span className="message-sender">{msg.sender || 'Unknown'}</span>
                                                {getMessageText(msg)}
                                            </div>
                                            <span className="message-timestamp">
                                                {formatTimestamp(msg.timestamp)}
                                            </span>
                                        </div>
                                    </ListGroup.Item>
                                )) : (
                                    <div className="empty-state">
                                        <div className="empty-state-icon">ðŸ’­</div>
                                        <p>No messages yet. Start the conversation!</p>
                                    </div>
                                )}
                            </ListGroup>

                            <Form className="message-input-form" onSubmit={e => { e.preventDefault(); handleSend(); }}>
                                <Form.Control
                                    type="text"
                                    placeholder="Type your message..."
                                    value={inputValue}
                                    onChange={e => setInputValue(e.target.value)}
                                    className="message-input"
                                />
                                <Button type="submit" className="send-btn">
                                    Send ðŸš€
                                </Button>
                            </Form>
                        </div>
                    ) : (
                        <div className="empty-state">
                            <div className="empty-state-icon">ðŸ‘†</div>
                            <p>Select a group to start chatting!</p>
                        </div>
                    )}
                </div>
            </Container>
        </div>
    );
}

export default GroupChatApp;
